package Java;

public class FinalKeyword {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // final value cannot be changed 
		// final keyword provide constant values
		// calculating salary then week variable will be final
		// prevents inheritance(if class is declared with final, that cannot be inherited)
		// prevents over riding
		final int a=10;
		 System.out.println(a);
		
	}
}
